const en = require('./en')
const es = require('./es')
const ar = require('./ar')
const tn = require('./tn')

function greet(lang) {
    if (lang === 'en') {
        en.greet()
    }
    if (lang === 'es') {
        es.greet()
    }
    if (lang === 'ar') {
        ar.greet()
    }
    if (lang === 'tn') {
        tn.greet()
    }
}

module.exports = greet